mapper.insert:
insert into category (id, seq, name
  )
values (#{id,jdbcType=INTEGER}, #{seq,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}
  )

mapper.update:
update category
set seq = #{seq,jdbcType=INTEGER},
  name = #{name,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, seq, name
from category
where id = #{id,jdbcType=INTEGER}
